class Solution {
    func furthestDistanceFromOrigin(_ moves: String) -> Int {
        var leftMoves = 0
        var rightMoves = 0
        var underscoreCount = 0

        // Count the occurrences of each move type
        for move in moves {
            switch move {
            case "L":
                leftMoves += 1
            case "R":
                rightMoves += 1
            case "_":
                underscoreCount += 1
            default:
                break
            }
        }

        // Calculate the initial distance based on L and R moves
        let initialDistance = abs(rightMoves - leftMoves)

        // The furthest distance can be the sum of the initial distance and all the '_' moves
        // Since '_' can be used to either extend the current direction further or move back towards the origin
        // to then move in the opposite direction for a potentially greater distance
        return initialDistance + underscoreCount
    }
}
