int alternatingSubarray(int* nums, int numsSize){
    if (numsSize < 2) return -1; // If there are fewer than two elements, no subarray can exist.

    int maxLength = -1; // To store the maximum length of alternating subarrays found.
    int currentLength = 1; // Tracks the length of the current alternating subarray being considered.

    // Iterate through the array starting from the second element.
    for (int i = 1; i < numsSize; ++i) {
        // Check if the current element and the previous element follow the alternating pattern.
        if ((i % 2 == 1 && nums[i] - nums[i - 1] == 1) || (i % 2 == 0 && nums[i - 1] - nums[i] == 1)) {
            // If yes, increment the current length.
            currentLength++;
        } else {
            // If not, reset the current length to 2 if the current and previous elements are consecutive.
            if (nums[i] - nums[i - 1] == 1 || nums[i - 1] - nums[i] == 1) {
                currentLength = 2;
            } else {
                // If the current and previous elements do not form a start of a new subarray, reset.
                currentLength = 1;
            }
        }
        // Update the maxLength with the maximum value between it and currentLength.
        maxLength = max(maxLength, currentLength);
    }

    // Return the maximum length of all found alternating subarrays or -1 if no such subarray exists.
    // Adjusting the return condition to ensure we don't return -1 when we actually found a valid subarray.
    return maxLength > 1 ? maxLength : -1;
}

// Helper function to get the maximum of two integers.
int max(int a, int b) {
    return a > b ? a : b;
}
