func alternatingSubarray(nums []int) int {
    maxLength := -1 // Initialize maxLength to -1 for cases where no alternating subarray exists
    currentLength := 1 // Start with 1 to include the first element of any potential subarray

    for i := 1; i < len(nums); i++ {
        // Check if the current element is part of an alternating sequence by comparing it to the previous one
        if (i % 2 == 1 && nums[i] - nums[i-1] == 1) || (i % 2 == 0 && nums[i] - nums[i-1] == -1) {
            currentLength++ // If it is, increment the currentLength
        } else if (i % 2 == 0 && nums[i] - nums[i-1] == 1) || (i % 2 == 1 && nums[i] - nums[i-1] == -1) {
            // If the sequence is still alternating but the pattern was reset (e.g., after a non-alternating pair),
            // reset currentLength to 2 to include the last two elements
            currentLength = 2
        } else {
            // If the current element breaks the alternating pattern, check if the currentLength
            // is greater than maxLength before resetting currentLength
            if currentLength > maxLength {
                maxLength = currentLength
            }
            currentLength = 1 // Reset the length for a new potential subarray
        }
    }

    // After the loop, check one last time if the last counted subarray is the longest
    if currentLength > maxLength {
        maxLength = currentLength
    }

    // Adjust the return value to account for the requirement that the subarray must be longer than 1
    if maxLength < 2 {
        return -1
    }

    return maxLength
}
