char* maximumOddBinaryNumber(char* s) {
    // Count the number of '1's in the string
    int countOnes = 0;
    for (int i = 0; s[i] != '\0'; i++) {
        if (s[i] == '1') {
            countOnes++;
        }
    }

    // Allocate memory for the result string
    // +1 for null terminator
    char* result = (char*)malloc(strlen(s) + 1);
    int index = 0;

    // Fill the result with '1's first, except one '1' that goes to the end
    for (int i = 0; i < countOnes - 1; i++) {
        result[index++] = '1';
    }

    // Then fill with '0's up to the second last position
    for (int i = countOnes - 1; i < strlen(s) - 1; i++) {
        result[index++] = '0';
    }

    // Ensure the last digit is '1' to make it an odd number
    result[index++] = '1';

    // Null terminate the string
    result[index] = '\0';

    return result;
}
