class Solution {
public:
    long long maximumTripletValue(vector<int>& nums) {
        long long maxVal = 0;
        int n = nums.size();
        // Keep track of the maximum and second maximum value seen so far from the left
        vector<int> maxLeft(n, 0), secondMaxLeft(n, 0);
        maxLeft[0] = nums[0];
        for (int i = 1; i < n; ++i) {
            if (nums[i] > maxLeft[i - 1]) {
                secondMaxLeft[i] = maxLeft[i - 1];
                maxLeft[i] = nums[i];
            } else {
                maxLeft[i] = maxLeft[i - 1];
                secondMaxLeft[i] = max(secondMaxLeft[i - 1], nums[i]);
            }
        }

        for (int k = 2; k < n; ++k) {
            // For each k, find the best possible (nums[i] - nums[j]) where i < j < k
            // This can be done by considering the second maximum value to the left of k
            // as nums[j] and the maximum value to the left of k as nums[i]
            if (maxLeft[k - 1] > secondMaxLeft[k - 1]) {
                long long currentValue = ((long long)maxLeft[k - 1] - secondMaxLeft[k - 1]) * nums[k];
                maxVal = max(maxVal, currentValue);
            }
        }
        return maxVal;
    }
};
