func maximumTripletValue(nums []int) int64 {
    n := len(nums)
    if n < 3 {
        return 0
    }

    // Initialize maxVal to store the maximum value of the triplet.
    maxVal := int64(0)

    // Iterate from the second last element down to the first element.
    for j := n - 2; j >= 1; j-- {
        // Find the maximum value in nums[k] for k > j.
        maxK := int64(0)
        for k := j + 1; k < n; k++ {
            if int64(nums[k]) > maxK {
                maxK = int64(nums[k])
            }
        }

        // For each j, find the best i (i < j) to maximize (nums[i] - nums[j]) * nums[k].
        for i := 0; i < j; i++ {
            // Calculate the current value for the triplet (i, j, k).
            currentValue := int64(nums[i]-nums[j]) * maxK
            if currentValue > maxVal {
                maxVal = currentValue
            }
        }
    }

    return maxVal
}
