class Solution {
    public long maximumTripletValue(int[] nums) {
        long maxProduct = 0;
        // Arrays to store the max difference up to the current index
        int[] maxDiff = new int[nums.length];

        // Initialize the first element of maxDiff
        maxDiff[0] = Integer.MIN_VALUE;  // Since there's no element before index 0

        // Populate the maxDiff array with the maximum difference seen so far
        for (int j = 1; j < nums.length; j++) {
            maxDiff[j] = Math.max(maxDiff[j - 1], nums[j - 1] - nums[j]);
        }

        // Iterate through the array to find the maximum product
        for (int k = 2; k < nums.length; k++) {
            if (maxDiff[k - 1] > 0) {  // Ensure the difference is positive
                maxProduct = Math.max(maxProduct, (long) maxDiff[k - 1] * nums[k]);
            }
        }

        return maxProduct;
    }
}
