class Solution {
    public long maximumTripletValue(int[] nums) {
        long maxVal = 0; // To track the maximum value of all triplets
        int n = nums.length;

        for (int i = 0; i < n - 2; i++) {
            for (int j = i + 1; j < n - 1; j++) {
                for (int k = j + 1; k < n; k++) {
                    // Calculate the value of the current triplet
                    long currentValue = (long) (nums[i] - nums[j]) * nums[k];
                    // Update maxVal if the current value is greater
                    maxVal = Math.max(maxVal, currentValue);
                }
            }
        }

        return maxVal;
    }
}
