class Solution {
    fun maximumTripletValue(nums: IntArray): Long {
        var maxVal = 0L // Use Long to avoid potential integer overflow
        val n = nums.size

        // Iterate through all possible triplets
        for (i in 0 until n) {
            for (j in i + 1 until n) {
                for (k in j + 1 until n) {
                    // Calculate the value of the current triplet
                    val currentValue = (nums[i] - nums[j]).toLong() * nums[k]
                    // Update maxVal if the current value is greater
                    if (currentValue > maxVal) {
                        maxVal = currentValue
                    }
                }
            }
        }

        return maxVal
    }
}
