impl Solution {
    pub fn maximum_triplet_value(nums: Vec<i32>) -> i64 {
        let mut max_value = 0i64; // Use i64 for the potential large values of the product
        let n = nums.len();

        // Iterate over k from the end to the start
        for k in 2..n {
            let mut max_diff = 0; // Track the maximum difference for this k
            // Iterate over i and j where i < j < k
            for i in 0..k {
                for j in (i+1)..k {
                    let diff = nums[i] - nums[j];
                    max_diff = max_diff.max(diff);
                }
            }
            // Calculate the value of the triplet and update max_value
            max_value = max_value.max((max_diff as i64) * (nums[k] as i64));
        }

        max_value
    }
}
