object Solution {
    def maximumTripletValue(nums: Array[Int]): Long = {
        var maxVal = 0L
        val n = nums.length
        val maxLeft = Array.fill(n)(0)
        val minLeft = Array.fill(n)(0)

        maxLeft(0) = nums(0)
        minLeft(0) = nums(0)
        for (i <- 1 until n) {
            maxLeft(i) = maxLeft(i - 1).max(nums(i))
            minLeft(i) = minLeft(i - 1).min(nums(i))
        }

        for (j <- 1 until n - 1) {
            if (nums(j) < maxLeft(j - 1)) {
                for (k <- j + 1 until n) {
                    val diff = (maxLeft(j - 1) - nums(j)) * nums(k)
                    if (diff > maxVal) maxVal = diff
                }
            }
        }

        maxVal
    }
}
