object Solution {
    def maximumTripletValue(nums: Array[Int]): Long = {
        val n = nums.length
        var maxProduct: Long = 0
        // Arrays to keep track of the maximum value from the right
        val maxRight = Array.fill[Long](n)(0)

        // Populate maxRight array
        maxRight(n - 1) = nums(n - 1)
        for (i <- (n - 2) to 0 by -1) {
            maxRight(i) = math.max(maxRight(i + 1), nums(i))
        }

        // Iterate through the array to find the maximum triplet value
        for (j <- 1 until n - 1) {
            var maxLeft = nums(0)
            // Find the maximum value to the left of j
            for (i <- 0 until j) {
                maxLeft = math.max(maxLeft, nums(i))
            }
            // Calculate the triplet value if it's positive
            val currentValue: Long = (maxLeft - nums(j)) * maxRight(j + 1)
            if (currentValue > maxProduct) {
                maxProduct = currentValue
            }
        }

        maxProduct
    }
}
