public class Solution {
    public int[] NumberGame(int[] nums) {
        int n = nums.Length;
        int[] arr = new int[n];
        List<int> numsList = new List<int>(nums);
        int arrIndex = 0;

        while (numsList.Count > 0) {
            // Sort the list to easily pick the minimum elements for Alice and Bob
            numsList.Sort();
            // Alice picks the minimum element
            int alicePick = numsList[0];
            // Remove Alice's pick
            numsList.RemoveAt(0);
            // Bob picks the next minimum (current minimum after Alice's pick)
            int bobPick = numsList[0];
            // Remove Bob's pick
            numsList.RemoveAt(0);

            // Append to arr in the order of Bob's choice first, then Alice's
            arr[arrIndex++] = bobPick;
            arr[arrIndex++] = alicePick;
        }

        return arr;
    }
}
