class Solution {
public:
    vector<int> numberGame(vector<int>& nums) {
        // Sort the nums array in ascending order to simulate the removal of minimum elements easily
        sort(nums.begin(), nums.end());

        vector<int> arr;
        int n = nums.size();

        // Since the game involves removing two elements in each round,
        // iterate until nums is empty with a step of 2 to simulate each round.
        for (int i = 0; i < n / 2; ++i) {
            // Bob's move: append the current minimum (i.e., first in the sorted array) to arr
            arr.push_back(nums[2 * i + 1]);
            // Alice's move: append the next minimum to arr
            arr.push_back(nums[2 * i]);
        }

        return arr;
    }
};
