#include <stdlib.h>

int compare(const void *a, const void *b) {
    return (*(int *)a - *(int *)b);
}

/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
int* numberGame(int* nums, int numsSize, int* returnSize) {
    *returnSize = numsSize;
    int* arr = (int*)malloc(numsSize * sizeof(int));
    if (!arr) return NULL; // Ensure memory allocation was successful

    // Sort nums array to make it easier to pick minimum elements
    qsort(nums, numsSize, sizeof(int), compare);

    // Implement game logic correctly: alternating removal and appending
    int left = 0, right = numsSize - 1;
    for (int i = 0; i < numsSize; i += 2) {
        // In each iteration, two elements are picked: the current minimum and the next minimum.
        // Bob's choice (next minimum) is placed in the array first, followed by Alice's choice (current minimum).
        // This ensures the order of elements in arr matches the expected outcome of the game.
        arr[i] = nums[right--]; // Bob's turn: append the largest remaining element
        if (i + 1 < numsSize) arr[i + 1] = nums[left++]; // Alice's turn: append the smallest remaining element
    }

    return arr;
}
