class Solution {
    fun numberGame(nums: IntArray): IntArray {
        val sortedNums = nums.sorted()
        val arr = IntArray(nums.size)
        for (i in sortedNums.indices step 2) {
            // Bob's turn first, then Alice's, for the arrangement in arr.
            arr[i] = sortedNums[i + 1] // Bob takes the higher number of the pair.
            arr[i + 1] = sortedNums[i] // Alice takes the lower number of the pair.
        }
        // Since the game alternates turns between Bob and Alice for removal,
        // and arr is filled alternately by Bob and Alice with their respective numbers,
        // the arrangement follows from sorting nums and distributing them in pairs.
        return arr
    }
}
