object Solution {
  def numberGame(nums: Array[Int]): Array[Int] = {
    // Sort the array to simplify element removal
    val sortedNums = nums.sorted
    val arr = Array.fill(nums.length)(0)
    var i = 0 // Index for the sortedNums
    var j = 0 // Index for the arr

    // Process elements according to game rules
    while (i < nums.length) {
      // Bob's turn to remove the second minimum (simulated by taking the last)
      arr(j) = sortedNums(i + 1)
      j += 1

      // Alice's turn to remove the first minimum
      arr(j) = sortedNums(i)
      j += 1

      i += 2 // Move to the next pair
    }

    arr
  }

  def main(args: Array[String]): Unit = {
    // Example 1
    val numsExample1 = Array(5, 4, 2, 3)
    val resultExample1 = Solution.numberGame(numsExample1)
    println(resultExample1.mkString("[", ", ", "]"))

    // Example 2
    val numsExample2 = Array(2, 5)
    val resultExample2 = Solution.numberGame(numsExample2)
    println(resultExample2.mkString("[", ", ", "]"))
  }
}
