class Solution {
    func numberGame(_ nums: [Int]) -> [Int] {
        // Copy of the input array to modify
        var numsCopy = nums
        // Resulting array
        var arr: [Int] = []

        while !numsCopy.isEmpty {
            // Sort the array to easily remove the minimum elements
            numsCopy.sort()
            // Alice removes the minimum element
            let alicePick = numsCopy.removeFirst()
            // Bob removes the next minimum element
            let bobPick = numsCopy.isEmpty ? alicePick : numsCopy.removeFirst()

            // According to the game rules, Bob appends first then Alice
            arr.append(bobPick)
            arr.append(alicePick)
        }

        return arr
    }
}
