class Solution {
public:
    int minOperations(vector<int>& nums, int k) {
        // Count of elements from 1 to k in the current window
        vector<int> count(k+1, 0);
        int totalCount = 0, left = 0, maxLen = 0;

        // Iterate over the array to find the longest subarray containing 1 to k
        for (int right = 0; right < nums.size(); ++right) {
            // Only interested in numbers 1 to k
            if (nums[right] <= k) {
                if (count[nums[right]] == 0) {
                    totalCount++; // Found a new required element
                }
                count[nums[right]]++;
            }

            // If we've found all elements from 1 to k, try to shrink the window from the left
            while (totalCount == k && left <= right) {
                maxLen = max(maxLen, right - left + 1);
                if (nums[left] <= k && --count[nums[left]] == 0) {
                    totalCount--; // Lost a unique required element
                }
                left++;
            }
        }

        // The minimum operations is the size of the array minus the longest subarray containing 1 to k
        return nums.size() - maxLen;
    }
};
