func minOperations(nums []int, k int) int {
    need := make(map[int]int)
    for i := 1; i <= k; i++ {
        need[i] = 1
    }

    found := 0
    left := 0
    minLen := len(nums) + 1 // Start with an impossible high value

    for right, num := range nums {
        // If num is one of the needed numbers, decrease the count in the map
        if need[num] > 0 {
            found++
        }
        need[num]--

        // Try to shrink the window from the left if the current window contains all 1 to k
        for found == k && left <= right {
            currentLen := right - left + 1
            if currentLen < minLen {
                minLen = currentLen
            }

            // Before moving left pointer, restore the count for the number at left if it's needed
            if need[nums[left]] == 0 {
                found--
            }
            need[nums[left]]++
            left++
        }
    }

    if minLen == len(nums) + 1 {
        // If minLen wasn't updated, it means it's not possible to find all 1...k, which should not happen as per constraints
        return len(nums)
    }

    // The minimum operations is the total length minus the longest subarray that doesn't need to be removed
    return len(nums) - minLen
}
