object Solution {
    def minOperations(nums: List[Int], k: Int): Int = {
        // HashSet to keep track of the numbers we need to collect
        val neededNumbers = scala.collection.mutable.HashSet[Int]((1 to k): _*)

        // Iterate over the array from the end
        var operations = 0
        for (num <- nums.reverse if neededNumbers.nonEmpty) {
            // Remove the number from the needed numbers if it is needed
            neededNumbers -= num
            // Increment the number of operations
            operations += 1
            // Break out of the loop if we have collected all needed numbers
            if (neededNumbers.isEmpty) return operations
        }

        // In case something goes wrong, but for the given constraints, it should not happen
        operations
    }
}
