class Solution {
    func minOperations(_ nums: [Int], _ k: Int) -> Int {
        // Count frequencies of each number up to k since we only care about numbers 1 to k.
        var count = Array(repeating: 0, count: k + 1)
        for num in nums where num <= k {
            count[num] += 1
        }

        // The total count of numbers from 1 to k in the array.
        let totalCount = count.reduce(0, +)

        // Use a sliding window to find the longest subsequence containing all numbers from 1 to k.
        var maxSequenceLength = 0
        var currentCount = Array(repeating: 0, count: k + 1)
        var start = 0
        for end in 0..<nums.count {
            if nums[end] <= k {
                currentCount[nums[end]] += 1
            }

            // If the window contains all numbers from 1 to k, try to shrink it while maintaining this property.
            while currentCount.filter({ $0 > 0 }).count == k {
                maxSequenceLength = max(maxSequenceLength, currentCount.reduce(0, +))
                if nums[start] <= k {
                    currentCount[nums[start]] -= 1
                }
                start += 1
            }
        }

        // The minimum operations needed is the total length of the array minus the length of this longest sequence.
        return nums.count - maxSequenceLength
    }
}
