function minOperations(nums: number[], k: number): number {
    let maxIndex = -1; // Keep track of the maximum index of elements 1 to k
    const lastIndexMap = new Map<number, number>(); // Map to store the last index of each element

    // Populate the map with the last index of each required element
    nums.forEach((num, index) => {
        if (num <= k) {
            lastIndexMap.set(num, index);
        }
    });

    // Find the maximum index of elements 1 through k to identify the shortest sequence
    for (let i = 1; i <= k; i++) {
        const index = lastIndexMap.get(i);
        if (index !== undefined) {
            maxIndex = Math.max(maxIndex, index);
        }
    }

    // The minimum operations required is the total elements minus the elements not needed for the sequence
    // This approach directly calculates the operations needed without explicitly removing elements
    return maxIndex + 1; // Adding 1 as the index is 0-based and we need the count
}

