function minOperations(nums: number[], k: number): number {
    let totalLength = nums.length;
    let maxLength = 0; // Track the length of the longest subsequence containing 1 to k

    // Use a sliding window to find the longest subsequence containing all numbers from 1 to k
    let left = 0; // Left index of the sliding window
    const count = new Array(k + 1).fill(0); // Count array to keep track of numbers 1 to k within the window
    let uniqueNumbers = 0; // Count of unique numbers from 1 to k in the current window

    for (let right = 0; right < totalLength; ++right) {
        if (nums[right] <= k && count[nums[right]] == 0) uniqueNumbers++; // Found a new unique number from 1 to k
        count[nums[right]]++;

        // If we have all numbers from 1 to k in the window, try to shrink the window from the left
        while (uniqueNumbers == k && left <= right) {
            maxLength = Math.max(maxLength, right - left + 1);
            // If the number at the left is part of the 1 to k set, update the window
            if (nums[left] <= k && count[nums[left]] == 1) uniqueNumbers--;
            count[nums[left]]--;
            left++;
        }
    }

    // If no valid subsequence is found, return the total operations as the length of the array
    // Else, return the difference between the total length and the maxLength of the subsequence found
    return uniqueNumbers == k ? totalLength - maxLength : totalLength;
}
