public class Solution {
    public int MinimumRightShifts(IList<int> nums) {
        // Check if already sorted
        bool isSorted = true;
        int n = nums.Count;
        int wrapPoints = 0, minIndex = 0;

        for (int i = 1; i < n; i++) {
            // Find the index of the minimum element
            if (nums[i] < nums[minIndex]) {
                minIndex = i;
            }
            // Check for wrap points indicating unsorted parts
            if (nums[i] < nums[i - 1]) {
                isSorted = false;
                wrapPoints++;
            }
        }

        // If already sorted, return 0
        if (isSorted) return 0;
        // If there are more than one wrap points, sorting is impossible
        if (wrapPoints > 1) return -1;

        // To sort the array, we need to bring the minimum element to the start
        // The minimum number of right shifts required is the distance of the minimum element from the end of the array
        // This is because a right shift moves the last element to the start
        return (n - minIndex) % n;
    }
}
