int minimumRightShifts(int* nums, int numsSize){
    // Step 1: Check if the array is already sorted
    int isSorted = 1, shiftPoint = -1;
    for(int i = 0; i < numsSize - 1; i++){
        if(nums[i] > nums[i + 1]){
            isSorted = 0;
            shiftPoint = i + 1;
            break;
        }
    }
    if(isSorted) return 0; // The array is already sorted

    // Step 2 and 3: Find the point of rotation and check for sortability
    int breakPoints = 0;
    for(int i = 0; i < numsSize; i++){
        if(nums[i] > nums[(i + 1) % numsSize]){
            breakPoints++;
            if(breakPoints > 1) return -1; // More than one breakpoint, not sortable
        }
    }

    // Step 4: Calculate the minimum right shifts
    // If the array is sortable, the minimum shifts required is the position of the shift point from the start
    return (numsSize - shiftPoint) % numsSize;
}
