class Solution {
  int minimumRightShifts(List<int> nums) {
    int n = nums.length;
    int rotationPoint = -1;
    // Check if array is already sorted or find the rotation point
    for (int i = 0; i < n; i++) {
      if (nums[i] > nums[(i + 1) % n]) {
        if (rotationPoint != -1) {
          // Found a second place where the array is "rotated" which means it's not sortable
          return -1;
        }
        rotationPoint = (i + 1) % n;
      }
    }
    // If the array is already sorted, no shifts are required.
    if (rotationPoint == -1) return 0;
    // To sort the array, we need to shift it n - rotationPoint times.
    // However, we need to ensure the resulting array is sorted.
    for (int i = 0; i < n; i++) {
      if (nums[(rotationPoint + i) % n] > nums[(rotationPoint + i + 1) % n]) {
        return -1; // The array cannot be sorted by right shifts.
      }
    }
    // Return the number of shifts required to sort the array.
    return rotationPoint == 0 ? 0 : n - rotationPoint;
  }
}
