class Solution {
  int minimumRightShifts(List<int> nums) {
    int n = nums.length;
    int descCount = 0; // Count of descending points
    int minIndex = 0; // To store the index of the minimum element

    // First, find the minimum element's index as it will be our pivot for rotation
    for (int i = 0; i < n; i++) {
      if (nums[i] < nums[minIndex]) {
        minIndex = i;
      }
    }

    // Check if array requires rotation and count descending points
    for (int i = 0; i < n; i++) {
      if (nums[i] > nums[(i + 1) % n]) {
        descCount++;
      }
    }

    // If there are more than one descending points, sorting is not possible
    if (descCount > 1) {
      return -1;
    }

    // The number of shifts is determined by the position of the minimum element
    // Since a sorted array rotated right will have its minimum element moved from the start
    return (n - minIndex) % n;
  }
}
