import java.util.List;

class Solution {
    public int minimumRightShifts(List<Integer> nums) {
        int n = nums.size();
        int breakPoint = -1; // To store the index where the sorted order breaks

        // Find the break point, if exists
        for (int i = 0; i < n - 1; i++) {
            if (nums.get(i) > nums.get(i + 1)) {
                // If we already found a break point, it means there are at least two breaks
                if (breakPoint != -1) {
                    return -1; // Cannot sort the array with right shifts
                }
                breakPoint = i + 1;
            }
        }

        // If there is no break point or the break is at the start, the array is already sorted
        if (breakPoint == -1 || breakPoint == 1) {
            return 0;
        }

        // Check if the portion after the breakpoint to the end is sorted in relation to the start of the array
        for (int i = breakPoint; i < n - 1; i++) {
            if (nums.get(i) > nums.get(i + 1)) {
                return -1; // Found another break in sorted order, cannot sort with right shifts
            }
        }

        // If the array can be sorted, the minimum right shifts required is from the break point to the end
        return n - breakPoint;
    }
}
