/**
 * @param {number[]} nums
 * @return {number}
 */
var minimumRightShifts = function(nums) {
    // Check if the array is already sorted
    let isSorted = true;
    for (let i = 1; i < nums.length; i++) {
        if (nums[i-1] > nums[i]) {
            isSorted = false;
            break;
        }
    }
    if (isSorted) return 0; // The array is already sorted

    let breakPoint = -1;
    for (let i = 0; i < nums.length - 1; i++) {
        // Find the index where the sequence breaks
        if (nums[i] > nums[i + 1]) {
            if (breakPoint !== -1) return -1; // More than one discontinuity
            breakPoint = i + 1;
        }
    }

    // Check if it is a single rotation of a sorted array
    for (let i = breakPoint; i < nums.length - 1; i++) {
        if (nums[i] > nums[i + 1]) return -1; // Not a single rotation
    }

    // Calculate the minimum number of shifts
    return nums.length - breakPoint;
};
