class Solution {
    fun minimumRightShifts(nums: List<Int>): Int {
        var shiftPoint = -1 // To track the index where the shift would start

        for (i in nums.indices) {
            // Check if the current element is greater than the next element (considering wrapping)
            if (nums[i] > nums[(i + 1) % nums.size]) {
                if (shiftPoint != -1) {
                    // Found more than one shift point, return -1
                    return -1
                }
                // Mark the shift start point
                shiftPoint = i
            }
        }

        // If array is already sorted or has a valid single rotation point
        return if (shiftPoint == -1) 0 else nums.size - (shiftPoint + 1)
    }
}
