class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function minimumRightShifts($nums) {
        $n = count($nums);
        $breakPoints = 0; // To count the sequence breaks.
        $minIndex = 0; // To keep track of the smallest element's index.

        // Find the index of the smallest element.
        for ($i = 1; $i < $n; $i++) {
            if ($nums[$i] < $nums[$minIndex]) {
                $minIndex = $i;
            }
        }

        // Count breaks in the sequence.
        for ($i = 0; $i < $n; $i++) {
            $next = ($i + 1) % $n;
            if ($nums[$i] > $nums[$next]) {
                $breakPoints++;
            }
        }

        // If there's more than one break, return -1 as it's not sortable by right shifts.
        if ($breakPoints > 1) {
            return -1;
        }

        // The minimum shifts required is the distance from the smallest element to the end of the array.
        // This is because after enough shifts, the smallest element will be at the beginning, and the array will be sorted.
        return ($n - $minIndex) % $n;
    }
}
