object Solution {
    def minimumRightShifts(nums: List[Int]): Int = {
        // Check if the array is already sorted
        if (nums.sorted == nums) return 0

        val minIndex = nums.indexOf(nums.min)

        // Split the array into two parts: before and after the minimum element
        val (beforeMin, afterMin) = nums.splitAt(minIndex)

        // Check if both parts are sorted and if the last element of the second part is less than the first element of the first part
        if ((beforeMin.isEmpty || beforeMin.head > afterMin.last) && afterMin.sorted == afterMin && beforeMin.sorted == beforeMin) {
            // The array is a rotated sorted array, return the minimum index
            minIndex
        } else {
            // The array cannot be sorted by right shifts
            -1
        }
    }
}
