object Solution {
    def minimumRightShifts(nums: List[Int]): Int = {
        // Initialize variables to track the index of deviation and the count of such deviations
        var deviationIndex = -1
        var deviations = 0

        // Iterate through the array to find deviations from the ascending order
        for(i <- 1 until nums.length) {
            if(nums(i) < nums(i - 1)) {
                deviationIndex = i
                deviations += 1
            }
        }

        // Check if the array is already sorted or if it has exactly one deviation and is sortable
        if(deviations == 0) {
            0 // Already sorted
        } else if(deviations == 1 && nums.head >= nums.last) {
            nums.length - deviationIndex // Calculate shifts needed to sort
        } else {
            -1 // More than one deviation or not sortable by shifts
        }
    }
}
