class Solution {
    func minimumRightShifts(_ nums: [Int]) -> Int {
        let n = nums.count
        var descCount = 0
        var shiftIndex = 0

        for i in 0..<n {
            if nums[i] > nums[(i + 1) % n] {
                descCount += 1
                shiftIndex = (i + 1) % n
            }
        }

        // If array is already sorted or if there's exactly one point of rotation
        if descCount == 0 {
            return 0
        } else if descCount == 1 {
            // The number of right shifts needed is equivalent to the position
            // of the element where the array should start to be sorted.
            return n - shiftIndex
        } else {
            // More than one point of rotation indicates it's impossible to sort
            // the array with right shifts alone.
            return -1
        }
    }
}
