class Solution {
    func minimumRightShifts(_ nums: [Int]) -> Int {
        // Check if the array is already sorted.
        var isSorted = true
        var shiftPoint = 0

        for i in 1..<nums.count {
            if nums[i-1] > nums[i] {
                // If the array is not sorted, mark the point and set isSorted to false.
                isSorted = false
                shiftPoint = i
                break
            }
        }

        if isSorted {
            // The array is already sorted, so return 0.
            return 0
        }

        // Check if the array can be sorted by a single right shift sequence.
        for i in 1..<nums.count {
            let prevIndex = (shiftPoint + i - 1) % nums.count
            let currentIndex = (shiftPoint + i) % nums.count
            if nums[prevIndex] > nums[currentIndex] {
                // If there's another point of inversion, it's not sortable by right shifts.
                return -1
            }
        }

        // Calculate the number of right shifts required to sort the array.
        return nums.count - shiftPoint
    }
}
