#include <vector>
#include <limits>

class Solution {
public:
    int minimumSum(vector<int>& nums) {
        int n = nums.size();
        int minSum = INT_MAX;
        bool found = false;

        for (int j = 1; j < n - 1; ++j) {
            int leftMin = INT_MAX;
            int rightMin = INT_MAX;

            // Find the smallest on the left
            for (int i = 0; i < j; ++i) {
                if (nums[i] < nums[j]) {
                    leftMin = min(leftMin, nums[i]);
                }
            }

            // Find the smallest on the right
            for (int k = j + 1; k < n; ++k) {
                if (nums[k] < nums[j]) {
                    rightMin = min(rightMin, nums[k]);
                }
            }

            // If valid mountain, update minSum
            if (leftMin != INT_MAX && rightMin != INT_MAX) {
                found = true;
                minSum = min(minSum, leftMin + nums[j] + rightMin);
            }
        }

        return found ? minSum : -1;
    }
};
