class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function minimumSum($nums) {
        $minSum = PHP_INT_MAX;
        $n = count($nums);

        // Precompute right min values for each index
        $rightMin = array_fill(0, $n, PHP_INT_MAX);
        for ($i = $n - 2; $i >= 0; $i--) {
            $rightMin[$i] = min($rightMin[$i + 1], $nums[$i + 1]);
        }

        for ($j = 1; $j < $n - 1; $j++) {
            $leftMin = PHP_INT_MAX;
            // Find the minimum value in the left part of the current index
            for ($i = 0; $i < $j; $i++) {
                if ($nums[$i] < $nums[$j]) {
                    $leftMin = min($leftMin, $nums[$i]);
                }
            }

            // Check if there's a smaller value on the right
            if ($nums[$j] > $rightMin[$j] && $leftMin < $nums[$j]) {
                $minSum = min($minSum, $leftMin + $nums[$j] + $rightMin[$j]);
            }
        }

        return $minSum == PHP_INT_MAX ? -1 : $minSum;
    }
}
