class Solution {
    func minimumSum(_ nums: [Int]) -> Int {
        let n = nums.count
        var minLeft = Array(repeating: Int.max, count: n)
        var minRight = Array(repeating: Int.max, count: n)

        // Compute minimum values to the left of each element
        for i in 1..<n {
            minLeft[i] = min(minLeft[i-1], nums[i-1])
        }

        // Compute minimum values to the right of each element
        for i in stride(from: n - 2, through: 0, by: -1) {
            minRight[i] = min(minRight[i+1], nums[i+1])
        }

        var minSum = Int.max
        for j in 1..<n-1 {
            if nums[j] > nums[j-1] && nums[j] > nums[j+1] && minLeft[j] < nums[j] && minRight[j] < nums[j] {
                let sum = minLeft[j] + nums[j] + minRight[j]
                minSum = min(minSum, sum)
            }
        }

        return minSum == Int.max ? -1 : minSum
    }
}
