function minimumSum(nums: number[]): number {
    let n = nums.length;
    let leftMin = new Array(n).fill(Number.MAX_VALUE);
    let rightMin = new Array(n).fill(Number.MAX_VALUE);

    // Fill leftMin array
    let minLeft = Number.MAX_VALUE;
    for (let i = 1; i < n; i++) {
        minLeft = Math.min(minLeft, nums[i-1]);
        leftMin[i] = minLeft;
    }

    // Fill rightMin array
    let minRight = Number.MAX_VALUE;
    for (let i = n - 2; i >= 0; i--) {
        minRight = Math.min(minRight, nums[i+1]);
        rightMin[i] = minRight;
    }

    // Find the minimum sum of a mountain triplet
    let minSum = Number.MAX_VALUE;
    for (let j = 1; j < n - 1; j++) {
        if (nums[j] > leftMin[j] && nums[j] > rightMin[j]) {
            minSum = Math.min(minSum, nums[j] + leftMin[j] + rightMin[j]);
        }
    }

    // Return the minimum sum if found, otherwise return -1
    return minSum === Number.MAX_VALUE ? -1 : minSum;
}
