#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int numberOfPoints(vector<vector<int>>& nums) {
        // Step 1: Sort the nums array based on the starting points.
        sort(nums.begin(), nums.end());

        int totalCovered = 0;
        int currentStart = nums[0][0];
        int currentEnd = nums[0][1];

        for (int i = 1; i < nums.size(); ++i) {
            // If the current car overlaps or is consecutive with the previous, merge them.
            if (nums[i][0] <= currentEnd + 1) {
                currentEnd = max(currentEnd, nums[i][1]);
            } else {
                // Add the length of the previous interval to the total covered length.
                totalCovered += currentEnd - currentStart + 1;
                // Start a new interval.
                currentStart = nums[i][0];
                currentEnd = nums[i][1];
            }
        }
        // Add the last interval's length to the total covered length.
        totalCovered += currentEnd - currentStart + 1;

        return totalCovered;
    }
};
