class Solution {

    /**
     * @param Integer[][] $nums
     * @return Integer
     */
    function numberOfPoints($nums) {
        // Sort intervals based on the start point
        usort($nums, function($a, $b) {
            return $a[0] - $b[0];
        });

        // Initialize merged intervals
        $merged = [];
        foreach ($nums as $interval) {
            // If merged is empty or current interval does not overlap with the last one in merged
            if (empty($merged) || $merged[count($merged) - 1][1] < $interval[0]) {
                array_push($merged, $interval);
            } else {
                // There's an overlap, so merge the current interval with the last one in merged
                $merged[count($merged) - 1][1] = max($merged[count($merged) - 1][1], $interval[1]);
            }
        }

        // Count the number of points covered by the merged intervals
        $count = 0;
        foreach ($merged as $interval) {
            $count += $interval[1] - $interval[0] + 1;
        }

        return $count;
    }
}
