function numberOfPoints(nums: number[][]): number {
    // Sort intervals based on the starting point.
    nums.sort((a, b) => a[0] - b[0]);

    let merged: number[][] = [];
    for (let interval of nums) {
        // If merged is empty or the current interval does not overlap with the last one in merged
        if (!merged.length || merged[merged.length - 1][1] < interval[0]) {
            merged.push(interval);
        } else {
            // If they overlap, merge them by updating the end of the last interval in merged
            merged[merged.length - 1][1] = Math.max(merged[merged.length - 1][1], interval[1]);
        }
    }

    // Calculate the total number of points covered by the merged intervals.
    let totalPoints = 0;
    for (let [start, end] of merged) {
        totalPoints += end - start + 1;
    }

    return totalPoints;
};
