impl Solution {
    pub fn semi_ordered_permutation(nums: Vec<i32>) -> i32 {
        // The strategy needs to involve a direct calculation of steps required
        // for 1 to reach the start and n to reach the end, considering their impact on each other.

        // Find positions of 1 and n in the array
        let n = nums.len() as i32;
        let pos_1 = nums.iter().position(|&x| x == 1).unwrap() as i32;
        let pos_n = nums.iter().position(|&x| x == n).unwrap() as i32;

        // Calculate swaps for 1 and n to reach their positions without directly adding their distances
        let mut swaps = 0;

        // Check if 1 and n are already in their correct positions
        if pos_1 == 0 && pos_n == n - 1 {
            return 0;
        }

        // For 1 to move to the beginning, swap it with each element to its left
        swaps += pos_1;

        // For n to move to the end, swap it with each element to its right
        swaps += n - 1 - pos_n;

        // If 1 and n are adjacent and out of place, reduce the total swaps by 1 as moving one towards its place
        // also moves the other away from its incorrect position
        if pos_1 + 1 == pos_n || pos_n + 1 == pos_1 {
            swaps -= 1;
        }

        swaps
    }
}
