public class Solution {
    public int MissingInteger(int[] nums) {
        // Sort the array to easily find the longest sequential prefix
        Array.Sort(nums);

        // Initialize variables for the longest sequential prefix
        int sum = 0, lastValid = 0;

        for (int i = 0; i < nums.Length; i++) {
            if (i == 0 || nums[i] == nums[i - 1] + 1) {
                // Add to sum if this is the first element or part of the sequential prefix
                sum += nums[i];
                lastValid = nums[i];
            } else if (nums[i] != nums[i - 1]) {
                // Break if the sequence is broken and it's not a duplicate
                break;
            }
        }

        // The starting point for checking the missing integer
        int start = lastValid + 1;

        // Check if the sum itself is missing
        if (!Array.Exists(nums, element => element == sum)) {
            return sum;
        }

        // Find the smallest missing integer greater than the sum
        while (true) {
            if (!Array.Exists(nums, element => element == start)) {
                return start;
            }
            start++;
        }
    }
}
