public class Solution {
    public int MissingInteger(int[] nums) {
        // Sort the nums array to find sequential numbers easily
        Array.Sort(nums);

        int maxSeqEnd = nums[0]; // Initialize with the first number
        int seqSum = nums[0]; // Initialize sum of the sequence
        bool seqBroken = false; // To track if the sequence has broken

        for (int i = 1; i < nums.Length && !seqBroken; i++) {
            // If current number is exactly 1 more than the previous, it's part of the sequence
            if (nums[i] == maxSeqEnd + 1) {
                maxSeqEnd = nums[i];
                seqSum += nums[i];
            } else if (nums[i] > maxSeqEnd + 1) {
                // Sequence is broken, exit the loop
                seqBroken = true;
            }
            // If nums[i] == maxSeqEnd, it's a duplicate and we ignore it but don't break the sequence
        }

        // Now, find the smallest missing integer greater than or equal to the sum of the longest sequential prefix
        int missingInt = seqSum + 1; // Start checking from one more than the sum
        while (true) {
            // If the missing integer is not in the array, return it
            if (!Array.Exists(nums, element => element == missingInt)) {
                return missingInt;
            }
            missingInt++;
        }
    }
}
