int missingInteger(int* nums, int numsSize) {
    int sum = nums[0];
    int length = 1;  // Length of the sequential prefix

    // Step 1: Find the longest sequential prefix
    for (int i = 1; i < numsSize; i++) {
        if (nums[i] == nums[i - 1] + 1) {
            sum += nums[i];
            length++;
        } else {
            break;  // Prefix ends if the sequence is broken
        }
    }

    // Step 2: Find the smallest missing integer greater than or equal to the sum
    int missingInt = sum;
    while (1) {
        int found = 0;  // Flag to check if missingInt is found in nums
        for (int i = 0; i < numsSize; i++) {
            if (nums[i] == missingInt) {
                found = 1;
                break;
            }
        }
        if (!found) {
            return missingInt;  // Return the first missing integer greater than or equal to the sum
        }
        missingInt++;  // Increment to check the next number
    }
}
