class Solution {
    public int missingInteger(int[] nums) {
        // Initialize variables to track the longest sequential prefix length and its sum.
        int maxLen = 1, currentLen = 1, sum = nums[0];

        // Loop through the array to find the longest sequential prefix.
        for (int i = 1; i < nums.length; i++) {
            // Check if the current number continues the sequence from the previous one.
            if (nums[i] == nums[i - 1] + 1) {
                // Increase the length of the current sequence and update the sum.
                currentLen++;
                sum += nums[i];
            } else {
                // If the current sequence is longer than the maximum found so far, update it.
                if (currentLen > maxLen) {
                    maxLen = currentLen;
                    // Sum of the longest sequential prefix.
                    sum = 0;
                    for (int j = i - currentLen; j < i; j++) {
                        sum += nums[j];
                    }
                }
                // Reset the length for the next sequence.
                currentLen = 1;
            }
        }

        // Handle the case where the longest sequence ends at the last element.
        if (currentLen > maxLen) {
            sum = 0;
            for (int i = nums.length - currentLen; i < nums.length; i++) {
                sum += nums[i];
            }
        }

        // Starting from the sum of the longest sequential prefix, find the smallest missing integer.
        int missingInt = sum;
        while (true) {
            boolean found = false;
            for (int num : nums) {
                if (num == missingInt) {
                    found = true;
                    break;
                }
            }
            if (!found) {
                // If missingInt is not found in the array, it's the answer.
                return missingInt;
            }
            // Otherwise, increment missingInt and check again.
            missingInt++;
        }
    }
}
