class Solution {
    public int missingInteger(int[] nums) {
        // Variables to track the longest sequential prefix and its sum.
        int longestSeqSum = nums[0];
        int currentSeqSum = nums[0];
        int prevNum = nums[0];
        int longestSeqLen = 1;
        int currentSeqLen = 1;

        // Loop through the array to find the longest sequential prefix.
        for (int i = 1; i < nums.length; i++) {
            // Check if the current number continues the sequence.
            if (nums[i] == prevNum + 1) {
                // Update the current sequence length and sum.
                currentSeqLen++;
                currentSeqSum += nums[i];
            } else {
                // If we find a number not in sequence, reset the current sequence length and sum.
                if (currentSeqLen > longestSeqLen) {
                    // Update the longest sequence details if current sequence is longer.
                    longestSeqLen = currentSeqLen;
                    longestSeqSum = currentSeqSum;
                }
                // Reset for the new sequence starting from the current number.
                currentSeqLen = 1;
                currentSeqSum = nums[i];
            }
            prevNum = nums[i];
        }

        // Check the last sequence in case it's the longest one.
        if (currentSeqLen > longestSeqLen) {
            longestSeqSum = currentSeqSum;
        }

        // Now, find the smallest missing integer greater than or equal to the sum of the longest sequential prefix.
        // Since the missing integer must be greater than or equal to the sum, start from the sum.
        for (int x = longestSeqSum; ; x++) {
            boolean found = false;
            for (int num : nums) {
                if (num == x) {
                    found = true;
                    break;
                }
            }
            // If x is not found in nums, then it's the smallest missing integer.
            if (!found) {
                return x;
            }
        }
    }
}
