/**
 * @param {number[]} nums
 * @return {number}
 */
var missingInteger = function(nums) {
    let longestSeqLength = 1;
    let currentSeqLength = 1;
    // Sorting the array to make it easier to find sequential numbers
    nums.sort((a, b) => a - b);

    // Identify the longest sequential prefix
    for (let i = 1; i < nums.length; i++) {
        if (nums[i] === nums[i - 1] + 1) {
            currentSeqLength++;
            longestSeqLength = Math.max(longestSeqLength, currentSeqLength);
        } else if (nums[i] !== nums[i - 1]) {
            // Reset currentSeqLength if the current number is not a direct continuation and not a duplicate
            currentSeqLength = 1;
        }
    }

    // Calculate the sum of the longest sequential prefix
    const firstValue = nums[0];
    const sumOfLongestSeqPrefix = firstValue + (longestSeqLength - 1) * longestSeqLength / 2;

    // Search for the smallest missing integer greater than or equal to the sum
    let missingInteger = sumOfLongestSeqPrefix;
    while (nums.includes(missingInteger)) {
        missingInteger++;
    }

    return missingInteger;
};
